{"changed":true,"filter":false,"title":"User.php","tooltip":"/microposts/app/User.php","value":"<?php\n\nnamespace App;\n\nuse Illuminate\\Notifications\\Notifiable;\nuse Illuminate\\Foundation\\Auth\\User as Authenticatable;\n\nclass User extends Authenticatable\n{\n    use Notifiable;\n\n    /**\n     * The attributes that are mass assignable.\n     *\n     * @var array\n     */\n    protected $fillable = [\n        'name', 'email', 'password',\n    ];\n    \n\n    /**\n     * The attributes that should be hidden for arrays.\n     *\n     * @var array\n     */\n    protected $hidden = [\n        'password', 'remember_token',\n    ];\n    \n    public function microposts()\n    {\n        return $this->hasMany(Micropost::class);\n    }\n    public function followings()\n    {\n        return $this->belongsToMany(User::class, 'user_follow', 'user_id', 'follow_id')->withTimestamps();\n    }\n\n    public function followers()\n    {\n        return $this->belongsToMany(User::class, 'user_follow', 'follow_id', 'user_id')->withTimestamps();\n    }\n    public function follow($userId)\n{\n    // confirm if already following\n    $exist = $this->is_following($userId);\n    // confirming that it is not you\n    $its_me = $this->id == $userId;\n\n    if ($exist || $its_me) {\n        // do nothing if already following\n        return false;\n    } else {\n        // follow if not following\n        $this->followings()->attach($userId);\n        return true;\n    }\n}\n\npublic function unfollow($userId)\n{\n    // confirming if already following\n    $exist = $this->is_following($userId);\n    // confirming that it is not you\n    $its_me = $this->id == $userId;\n\n\n    if ($exist && !$its_me) {\n        // stop following if following\n        $this->followings()->detach($userId);\n        return true;\n    } else {\n        // do nothing if not following\n        return false;\n    }\n}\n\n\npublic function is_following($userId) {\n    return $this->followings()->where('follow_id', $userId)->exists();\n}\n\npublic function feed_microposts()\n    {\n        $follow_user_ids = $this->followings()-> pluck('users.id')->toArray();\n        $follow_user_ids[] = $this->id;\n        return Micropost::whereIn('user_id', $follow_user_ids);\n    }\n    \npublic function favoritings()\n    {\n        return $this->belongsToMany(Micropost::class, 'favorite', 'user_id', 'favorite_id')->withTimestamps();\n    }\npublic function favorite($micropostId)\n{\n    $exist = $this->is_favoriting($micropostId);\n    \n    if ($exist){\n        return false;\n    }\n    else{\n        $this->favoritings()->attach($micropostId);\n        return true;\n    }\n}\npublic function unfavorite($micropostId)\n{\n    $exist = $this->is_favoriting($micropostId);\n    if ($exist){\n        $this->favoritings()->detach($micropostId);\n        return true;\n    } else {\n        return false;\n    }\n}\npublic function is_favoriting($micropostId) {\n    return $this->favoritings()->where('favorite_id', $micropostId)->exists();\n}\n    \n}","undoManager":{"mark":-2,"position":100,"stack":[[{"start":{"row":103,"column":19},"end":{"row":103,"column":20},"action":"insert","lines":[";"],"id":153}],[{"start":{"row":104,"column":5},"end":{"row":105,"column":0},"action":"insert","lines":["",""],"id":154},{"start":{"row":105,"column":0},"end":{"row":105,"column":4},"action":"insert","lines":["    "]},{"start":{"row":105,"column":4},"end":{"row":106,"column":0},"action":"insert","lines":["",""]},{"start":{"row":106,"column":0},"end":{"row":106,"column":4},"action":"insert","lines":["    "]}],[{"start":{"row":106,"column":0},"end":{"row":106,"column":4},"action":"remove","lines":["    "],"id":155},{"start":{"row":105,"column":4},"end":{"row":106,"column":0},"action":"remove","lines":["",""]}],[{"start":{"row":105,"column":4},"end":{"row":106,"column":0},"action":"insert","lines":["",""],"id":156},{"start":{"row":106,"column":0},"end":{"row":106,"column":4},"action":"insert","lines